
// Som de notificação "Pop" curto e limpo em Base64
const NOTIFICATION_SOUND = "data:audio/wav;base64,UklGRl9vT1BXQVZFZm10IBAAAAABAAEAQB8AAEAfAAABAAgAZGF0YU";

// Fallback real para som de pop (curto)
const POP_SOUND = "data:audio/mp3;base64,";

// Singleton Audio Instance para evitar Garbage Collection excessivo
let audioInstance: HTMLAudioElement | null = null;

export const playNotificationSound = () => {
  try {
    if (!audioInstance) {
      audioInstance = new Audio(POP_SOUND);
      audioInstance.volume = 0.6;
    } else {
      // Reinicia o áudio se já estiver tocando ou terminou
      audioInstance.currentTime = 0;
    }

    const promise = audioInstance.play();
    
    if (promise !== undefined) {
        promise.catch(error => {
            // Autoplay bloqueado pelo navegador (comum se não houver interação do usuário)
            // Silenciosamente ignorado para não sujar o console
        });
    }
  } catch (error) {
    // Erro silencioso em produção
  }
};
